Motor.ACT_TurnForwards();
IF(PicAlpha_DestinationPosition_PickUp2=PicAlpha_OnPickUp2 AND PicAlpha_OnPickUp2)THEN
	CurrentEqualsDestination:=TRUE;
ELSIF(PicAlpha_DestinationPosition_PutDown1=PicAlpha_OnPutDown1 AND PicAlpha_OnPutDown1 )THEN
	CurrentEqualsDestination:=TRUE;	
ELSIF(PicAlpha_DestinationPosition_PutDown2=PicAlpha_OnPutDown2 AND PicAlpha_OnPutDown2)THEN
	CurrentEqualsDestination:=TRUE;
END_IF

PicAlpha_WPPickedUp:=FALSE;
PicAlpha_DestinationPosition_PickUp1:=FALSE;
PicAlpha_DestinationPosition_PickUp2:=FALSE;
PicAlpha_DestinationPosition_PutDown1:=FALSE;
PicAlpha_DestinationPosition_PutDown2:=FALSE;

Gripper.ACT_Release();
PicAlphaVacuumHit:=FALSE;	
Motor.ACT_Stop();

CurrentEqualsDestination:=FALSE;
PicAlphaMoved:=FALSE;

Motor.ACT_TurnBackwards();
CurrentEqualsDestination:=FALSE;
IF(PicAlpha_DestinationPosition_PickUp1=PicAlpha_OnPickUp1 AND PicAlpha_OnPickUp1)THEN
	CurrentEqualsDestination:=TRUE;
ELSIF(PicAlpha_DestinationPosition_PickUp2=PicAlpha_OnPickUp2 AND PicAlpha_OnPickUp2)THEN
	CurrentEqualsDestination:=TRUE;
ELSIF(PicAlpha_DestinationPosition_PutDown1=PicAlpha_OnPutDown1 AND PicAlpha_OnPutDown1)THEN
	CurrentEqualsDestination:=TRUE;	
END_IF

