Stack_WPType_White:=FALSE;
Stack_WPType_Black:=FALSE;
Stack_WPType_Metallic:=FALSE;
Stack_WPWeight_Light:=FALSE;
Stack_WPWeight_Medium:=FALSE;
Stack_WPWeight_Heavy:=FALSE;

IF(NOT Stack_InductiveSensor AND NOT Stack_OpticalSensor)THEN
	Stack_WPType_Black:=TRUE;
	Stack_WPType_White:=FALSE;
	Stack_WPType_Metallic:=FALSE;
ELSIF(NOT Stack_InductiveSensor AND Stack_OpticalSensor)THEN
	Stack_WPType_Black:=FALSE;
	Stack_WPType_White:=TRUE;
	Stack_WPType_Metallic:=FALSE;
ELSIF(Stack_InductiveSensor)THEN
	Stack_WPType_Black:=FALSE;
	Stack_WPType_White:=FALSE;
	Stack_WPType_Metallic:=TRUE;
END_IF

IF(Stack_WPType_Black OR Stack_WPType_White OR(Stack_WPType_Metallic AND Stack_WPWeight < 12000))THEN
	Stack_WPWeight_Light:=TRUE;
	Stack_WPWeight_Medium:=FALSE;
	Stack_WPWeight_Heavy:=FALSE;
ELSIF(12000<Stack_WPWeight AND Stack_WPWeight<=24000)THEN
	Stack_WPWeight_Light:=FALSE;
	Stack_WPWeight_Medium:=TRUE;
	Stack_WPWeight_Heavy:=FALSE;	
ELSIF(24000<=Stack_WPWeight)THEN
	Stack_WPWeight_Light:=FALSE;
	Stack_WPWeight_Medium:=FALSE;
	Stack_WPWeight_Heavy:=TRUE;	
END_IF

Stack_WPProvided:=TRUE;

Stack_WPType_Metallic:=TRUE;
IF(Stack_WPWeight < 12000)THEN
	Stack_WPWeight_Light:=TRUE;
END_IF
IF(12000<Stack_WPWeight AND Stack_WPWeight<=24000)THEN
	Stack_WPWeight_Medium:=TRUE;
END_IF
IF(24000<=Stack_WPWeight)THEN
	Stack_WPWeight_Heavy:=TRUE;
END_IF

Stack_WPType_White:=FALSE;
Stack_WPType_Black:=FALSE;
Stack_WPType_Metallic:=FALSE;
Stack_WPWeight_Light:=FALSE;
Stack_WPWeight_Medium:=FALSE;
Stack_WPWeight_Heavy:=FALSE;
Stack_WPProvided:=FALSE;

Seperator.ACT_Retract();

